import React, { Component } from "react";
import "./Form.css";
import { jsPDF } from "jspdf";

class Form extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      course: "",
      isNameValid: false,
      isCourseValid: false,
    };
  }

  handleNameChange = (e) => {
    const name = e.target.value;
    const isNameValid = name.trim() !== "";
    this.setState({ name, isNameValid });
  };

  handleCourseChange = (e) => {
    const course = e.target.value;
    const isCourseValid = course !== "";
    this.setState({ course, isCourseValid });
  };

  generatePDF = () => {
    const { name, course, isNameValid, isCourseValid } = this.state;
    if (!isNameValid || !isCourseValid) {
      alert("Please fill out both name and course before generating PDF.");
      return;
    }

    const doc = new jsPDF();
    const currentDate = new Date().toLocaleDateString();
    let template;
    if (course === "B.tech") {
      template = `Congratulations ${name}  for B.tech program.\nDate of offer: ${currentDate}`;
    } else if (course === "M.tech") {
      template = `Congratulations ${name}  for M.tech program.\nDate of offer: ${currentDate}`;
    }
    doc.text(template, 10, 10);
    doc.save(`${name}_${course}.pdf`);
  };

  render() {
    const { name, course, isNameValid, isCourseValid } = this.state;
    return (
      <div>
        <div className="Formpage">
          <div className="Formpage-name">
            Name:
            <label>
              <input
                className="Formpage-input"
                type="text"
                value={name}
                onChange={this.handleNameChange}
              />
            </label>
            {!isNameValid && <span className="error"></span>}
          </div>
          <div className="Formpage-course">
            Course:
            <label>
              <select
                className="Formpage-select"
                value={course}
                onChange={this.handleCourseChange}
              >
                <option value="">Select course</option>
                <option value="B.tech">B.tech</option>
                <option value="M.tech">M.tech</option>
              </select>
            </label>
            {!isCourseValid && <span className="error"></span>}
          </div>
          <div className="Formpage-button">
            <button className="Formpage-button-submit">Submit</button>
            <button
              className="Formpage-button-generatePDF"
              onClick={this.generatePDF}
            >
              Generate PDF
            </button>
          </div>
        </div>
      </div>
    );
  }
}

export default Form;
