import React, { Component } from "react";
import "./Form.css";
import { jsPDF } from "jspdf";

class Form extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      course: "B.tech",
    };
  }

  handleNameChange = (e) => {
    this.setState({ name: e.target.value });
  };

  handleCourseChange = (e) => {
    this.setState({ course: e.target.value });
  };

  generatePDF = () => {
    const { name, course } = this.state;
    const doc = new jsPDF();
    const currentDate = new Date().toLocaleDateString();
    let template;
    if (course === "B.tech") {
      template = `Congratulations ${name}  for B.tech program.\nDate of offer: ${currentDate}`;
    } else if (course === "M.tech") {
      template = `Congratulations ${name}  for M.tech program.\nDate of offer: ${currentDate}`;
    }
    doc.text(template, 10, 10);
    doc.save(`${name}_offer_letter.pdf`);
  };

  render() {
    const { name, course } = this.state;
    return (
      <div>
        <div className="Formpage">
          <div>
            <label>
              Name:
              <input
                type="text"
                value={name}
                onChange={this.handleNameChange}
              />
            </label>
          </div>
          <div>
            <label>
              Course:
              <select value={course} onChange={this.handleCourseChange}>
                <option value="B.tech">B.tech</option>
                <option value="M.tech">M.tech</option>
              </select>
            </label>
          </div>
          <div>
            <button>Submit</button>
            <button onClick={this.generatePDF}>Generate PDF</button>
          </div>
        </div>
      </div>
    );
  }
}

export default Form;
