import React, { useState } from "react";
// import { jsPDF } from "jspdf";

const Form = () => {
  const [name, setName] = useState("");
  const [course, setCourse] = useState("B.tech");

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleCourseChange = (e) => {
    setCourse(e.target.value);
  };

  const generatePDF = () => {
    const doc = new jsPDF();

    // Set the date
    const currentDate = new Date().toLocaleDateString();

    // Set the template based on the course
    let template;
    if (course === "B.tech") {
      template = `Congratulations ${name}! You have been selected for B.tech program.\nDate of offer: ${currentDate}`;
    } else if (course === "M.tech") {
      template = `Congratulations ${name}! You have been selected for M.tech program.\nDate of offer: ${currentDate}`;
    }

    // Add text to the PDF
    doc.text(template, 10, 10);

    // Save the PDF
    doc.save("offer_letter.pdf");
  };

  return (
    <div>
      <label>
        Name:
        <input type="text" value={name} onChange={handleNameChange} />
      </label>
      <br />
      <label>
        Course:
        <select value={course} onChange={handleCourseChange}>
          <option value="B.tech">B.tech</option>
          <option value="M.tech">M.tech</option>
        </select>
      </label>
      <br />
      <button onClick={generatePDF}>Generate PDF</button>
    </div>
  );
};

export default Form;
